    public static int longestValidParentheses(String s) {
//        int[][] memo = new int[s.length()][s.length()];
//        for (int[] ints : memo) {
//            Arrays.fill(ints, -1);
//        }
        int[] memo = new int[s.length()];
        Arrays.fill(memo, -1);
        fancyHelper(s.toCharArray(), 0, memo);
        int res = 0;
        for (int i : memo) {
            res = Math.max(i, res);
        }
        return res;
    }

    static int fancyHelper(char[] ch, int idx, int[] memo) {
        if (idx >= ch.length - 1) {
            return 0;
        }
        if (idx == ch.length - 2) {
            if (ch[idx] == '(' && ch[idx + 1] == ')') {
                return 2;
            }
            return 0;
        }
        if (memo[idx] != -1) return memo[idx];
        if (ch[idx] == '(' && ch[idx + 1] == ')') {
//            return 2 + fancyHelper(ch, idx + 2, memo);
            return memo[idx] = 2 + fancyHelper(ch, idx + 2, memo);
        }
        int intermidate = fancyHelper(ch, idx + 1, memo);
        memo[idx] = intermidate;
        if (ch[idx] == '(' && ch[idx + 1] == '(' && idx + intermidate + 1 < ch.length && ch[idx + intermidate + 1] == ')') {
//            return 2 + intermidate + fancyHelper(ch, idx + intermidate + 2, memo);
            return memo[idx] = 2 + intermidate + fancyHelper(ch, idx + intermidate + 2, memo);
        }
//        return 0;
        return memo[idx] = 0;
    }

    static int helper(char[] ch, int s, int e, int[][] memo) {
        if (s == e) return 0;
        if (memo[s][e] != -1) return memo[s][e];
        if (s + 1 == e) {
            if (ch[s] == '(' && ch[e] == ')') {
                return memo[s][e] = 2;
            } else {
                return 0;
            }
        }
        int res = 0;
        if (ch[s] == '(' && ch[e] == ')') {
            int cnt = 0;
            for (int i = s; i < e; i++) {
                if (ch[i] == '(') {
                    cnt++;
                }
                if (ch[i] == ')') {
                    cnt--;
                }
                if (cnt == 0) {
                    int left = helper(ch, s, i, memo);
                    int right = helper(ch, i + 1, e, memo);
                    if (left + right == e - s + 1) {
                        return memo[s][e] = left + right;
                    } else {
                        if (left > right) {
                            res = left;
                            memo[s][i] = left;
                        } else {
                            res = right;
                            memo[i + 1][e] = right;
                        }
                        break;
                    }
                }
            }
            if (res == 0) {
                int outerType = helper(ch, s + 1, e - 1, memo);
                if (outerType == e - s - 1) {
                    return memo[s][e] = 2 + outerType;
                } else {
                    res = outerType;
                }
            }
        }
        int left = helper(ch, s + 1, e, memo);
        if (left == e - s) {
            return memo[s][e] = left;
        }
        int right = helper(ch, s, e - 1, memo);
        if (right == e - s) {
            return memo[s][e] = right;
        }
        return memo[s][e] = Math.max(res, Math.max(left, right));
    }

    static int max3(int a, int b, int c) {
        int res = a;
        if (b > res) {
            res = b;
        }
        if (c > res) {
            res = c;
        }
        return res;
    }
